async function loadBlogPosts(){try{const a=await fetch("js/data/blog-posts.json");if(!a.ok)throw new Error("Failed to load blog posts");displayBlogPosts((await a.json()).posts)}catch(a){console.error("Error loading blog posts:",a),displayError("Failed to load blog posts. Please try again later.")}}function displayBlogPosts(a){const o=document.querySelector(".blog-grid");if(!o)return;const e=[...a].sort((a,o)=>new Date(o.date)-new Date(a.date)).slice(0,3).map(a=>`\n        <article class="blog-card">\n            <div class="blog-card-image">\n                <img src="${a.image||"images/blog-placeholder.jpg"}" alt="${a.title}">\n                <div class="blog-category">${a.category}</div>\n            </div>\n            <div class="blog-card-content">\n                <div class="blog-meta">\n                    <span class="blog-date">${formatDate(a.date)}</span>\n                    <span class="blog-read-time">${a.read_time||"5 min read"}</span>\n                </div>\n                <h3 class="blog-title">${a.title}</h3>\n                <p class="blog-excerpt">${a.excerpt}</p>\n                <a href="blog/${a.slug}.html" class="blog-read-more">Read More <i class="fas fa-arrow-right"></i></a>\n            </div>\n        </article>\n    `).join("");o.innerHTML=e}function formatDate(a){return new Date(a).toLocaleDateString("en-US",{year:"numeric",month:"long",day:"numeric"})}function displayError(a){const o=document.querySelector(".blog-grid");o&&(o.innerHTML=`\n            <div class="error-message">\n                <i class="fas fa-exclamation-circle"></i>\n                <p>${a}</p>\n            </div>\n        `)}document.addEventListener("DOMContentLoaded",function(){loadBlogPosts()});