function initMap(){if(!document.getElementById("research-map"))return;const e=L.map("research-map").setView([20.5937,78.9629],5);L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png",{attribution:'&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',maxZoom:18}).addTo(e);const o=[{name:"Western Ghats",coords:[10.0889,77.0595],description:"Biodiversity hotspot with high endemism",type:"field_site",date:"2023",icon:"leaf",iconColor:"#2E8B57"},{name:"Kerala",coords:[10.8505,76.2711],description:"Tropical rainforest ecosystem studies",type:"field_site",date:"2022-2023",icon:"tree",iconColor:"#2E8B57"},{name:"Tamil Nadu",coords:[11.1271,78.6569],description:"Avian diversity research",type:"field_site",date:"2021",icon:"dove",iconColor:"#2E8B57"},{name:"Karnataka",coords:[15.3173,75.7139],description:"Bioacoustic monitoring project",type:"field_site",date:"2022",icon:"headphones",iconColor:"#2E8B57"}];if(o.forEach(o=>{var i,a;L.marker(o.coords,{icon:(i=o.icon,a=o.iconColor,L.divIcon({html:`<div style="color: ${a}; font-size: 24px;"><i class="fas fa-${i}"></i></div>`,className:"custom-marker",iconSize:[30,30],iconAnchor:[15,30],popupAnchor:[0,-30]}))}).addTo(e).bindPopup(`\n            <div class="map-popup">\n                <h4>${o.name}</h4>\n                <p>${o.description}</p>\n                <div class="map-popup-meta">\n                    <span><i class="fas fa-calendar-alt"></i> ${o.date}</span>\n                    <span><i class="fas fa-${o.icon}"></i> ${o.type.replace("_"," ")}</span>\n                </div>\n            </div>\n        `)}),o.length>0){const i=o.map(e=>e.coords);e.fitBounds(i,{padding:[50,50]})}L.control.scale({imperial:!1}).addTo(e)}document.addEventListener("DOMContentLoaded",initMap);